import React from "react";

function Immer(){

    //리액트는 불변성을 유지해야한다.
    const todos = [
        {
          id: 1,
          text: '할 일 #1',
          done: true
        },
        {
          id: 2,
          text: '할 일 #2',
          done: false
        }
      ];

      const inserted = todos.concat({
        id: 3,
        text: '할 일 #3',
        done: false
      });

      const filtered = todos.filter(todo => todo.id !== 2);

      const toggled = todos.map(
        todo => todo.id === 2
          ? {
            ...todo,
            done: !todo.done,
          }
          : todo
      );

      const state = {
        posts: [
          {
            id: 1,
            title: '제목입니다.',
            body: '내용입니다.',
            comments: [
              {
                id: 1,
                text: '와 정말 잘 읽었습니다.'
              }
            ]
          },
          {
            id: 2,
            title: '제목입니다.',
            body: '내용입니다.',
            comments: [
              {
                id: 2,
                text: '또 다른 댓글 어쩌고 저쩌고'
              }
            ]
          }
        ],
        selectedId: 1
      };

    return(
        console.log(todos),
        console.log(inserted),
        console.log(filtered),
        console.log(toggled)

    );
}

export default Immer;
